import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaEnvelope, FaDownload, FaArrowDown, FaRocket, FaChartLine, FaUsers } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  background: ${props => props.isDarkMode ? props.theme.gradients.glassDark : props.theme.gradients.hero};
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${props => props.isDarkMode ? 
      'radial-gradient(circle at 20% 80%, rgba(56, 189, 248, 0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(240, 171, 252, 0.1) 0%, transparent 50%)' :
      'radial-gradient(circle at 20% 80%, rgba(14, 165, 233, 0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(217, 70, 239, 0.1) 0%, transparent 50%)'
    };
    pointer-events: none;
  }
`;

const BackgroundGradient = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: ${props => props.isDarkMode ? 
    'linear-gradient(135deg, rgba(10, 10, 10, 0.8) 0%, rgba(26, 26, 26, 0.9) 100%)' :
    'linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(248, 250, 252, 0.95) 100%)'
  };
  z-index: 1;
`;

const FloatingElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;
  overflow: hidden;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  width: 60px;
  height: 60px;
  background: ${props => props.theme.colors.primary[200]};
  border-radius: ${props => props.theme.borderRadius.full};
  opacity: 0.1;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.theme.colors.primary[600]};
  font-size: ${props => props.theme.fonts.sizes.xl};
`;

const HeroContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing['4xl']};
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.lg};
  position: relative;
  z-index: 2;
  min-height: 80vh;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing['3xl']};
    text-align: center;
  }

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.md};
    gap: ${props => props.theme.spacing['2xl']};
  }
`;

const HeroText = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.xl};
`;

const Greeting = styled(motion.h2)`
  font-size: ${props => props.theme.fonts.sizes['2xl']};
  font-weight: ${props => props.theme.fonts.weights.medium};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  margin-bottom: ${props => props.theme.spacing.sm};
  letter-spacing: 0.5px;
`;

const Name = styled(motion.h1)`
  font-size: ${props => props.theme.fonts.sizes['7xl']};
  font-weight: ${props => props.theme.fonts.weights.black};
  background: ${props => props.theme.gradients.primary};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: ${props => props.theme.spacing.md};
  letter-spacing: -1px;
  line-height: 0.9;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    font-size: ${props => props.theme.fonts.sizes['5xl']};
  }

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    font-size: ${props => props.theme.fonts.sizes['4xl']};
  }
`;

const Title = styled(motion.h3)`
  font-size: ${props => props.theme.fonts.sizes['2xl']};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.secondary : props.theme.colors.light.text.secondary};
  margin-bottom: ${props => props.theme.spacing.lg};
  letter-spacing: 0.5px;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    font-size: ${props => props.theme.fonts.sizes.xl};
  }
`;

const Description = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  line-height: ${props => props.theme.fonts.lineHeights.relaxed};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.tertiary : props.theme.colors.light.text.tertiary};
  margin-bottom: ${props => props.theme.spacing.xl};
  max-width: 500px;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    max-width: 100%;
  }
`;

const Stats = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
  grid-template-columns: repeat(2, 1fr);
    gap: ${props => props.theme.spacing.md};
  }

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const Stat = styled.div`
  text-align: center;
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.xs};
  letter-spacing: -0.5px;
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.secondary : props.theme.colors.light.text.secondary};
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    flex-direction: column;
    align-items: center;
  }
`;

const PrimaryButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.xl};
  background: ${props => props.theme.gradients.primary};
  color: white;
  border-radius: ${props => props.theme.borderRadius.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.lg};
  border: none;
  cursor: pointer;

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.xl};
    color: white;
  }

  &:active {
    transform: translateY(0);
  }
`;

const SecondaryButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.xl};
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.md};

  &:hover {
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.lg};
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  }
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-top: ${props => props.theme.spacing.lg};
`;

const SocialLink = styled(motion.a)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 50px;
  height: 50px;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius.full};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.md};

  &:hover {
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.lg};
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  }
`;

const HeroVisual = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ProfileCard = styled(motion.div)`
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius['3xl']};
  padding: ${props => props.theme.spacing['2xl']};
  box-shadow: ${props => props.theme.shadows.glass};
  max-width: 400px;
  width: 100%;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: ${props => props.theme.gradients.primary};
  }

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    max-width: 350px;
    padding: ${props => props.theme.spacing.xl};
  }
`;

         const ProfileImage = styled.div`
           width: 120px;
           height: 120px;
           border-radius: ${props => props.theme.borderRadius.full};
           overflow: hidden;
           margin: 0 auto ${props => props.theme.spacing.lg};
           border: 4px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.accent : props.theme.colors.light.border.accent};
           box-shadow: ${props => props.theme.shadows.lg};

           img {
    width: 100%;
    height: 100%;
             object-fit: cover;
           }
         `;

const ProfileInfo = styled.div`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
`;

const ProfileName = styled.h3`
  font-size: ${props => props.theme.fonts.sizes['2xl']};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const ProfileRole = styled.p`
  font-size: ${props => props.theme.fonts.sizes.md};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.md};
  font-weight: ${props => props.theme.fonts.weights.semibold};
`;

const ProfileStats = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.md};
`;

const ProfileStat = styled.div`
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.secondary : props.theme.colors.light.border.secondary};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.md};
  text-align: center;
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.md};
  }
`;

const ProfileStatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.xs};
`;

