const Message = styled(motion.div)`
  padding: 15px;
  border-radius: ${props => props.theme.borderRadius.md};
  margin-bottom: 20px;
  text-align: center;
  font-weight: ${props => props.theme.fonts.weights.medium};

  ${props => props.type === 'success' && `
    background: ${props.theme.colors.status.success}20;
    border: 1px solid ${props.theme.colors.status.success}40;
    color: ${props.theme.colors.status.success};
  `}

  ${props => props.type === 'error' && `
    background: ${props.theme.colors.status.error}20;
    border: 1px solid ${props.theme.colors.status.error}40;
    color: ${props.theme.colors.status.error};
  `}
`;

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage(null);

    try {
      const response = await fetch('/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setMessage({ type: 'success', text: 'Thank you! Your message has been sent successfully.' });
        setFormData({ name: '', email: '', subject: '', message: '' });
      } else {
        setMessage({ type: 'error', text: 'Failed to send message. Please try again.' });
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'An error occurred. Please try again later.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const contactInfo = [
    {
      icon: <FaEnvelope />,
      label: 'Email',
      value: 'omprakashutaha@gmail.com',
      link: 'mailto:omprakashutaha@gmail.com'
    },
    {
      icon: <FaPhone />,
      label: 'Phone',
      value: '+91 9723231499',
      link: 'tel:+919723231499'
    },
    {
      icon: <FaMapMarkerAlt />,
      label: 'Location',
      value: 'Bengaluru, Karnataka, India'
    }
  ];

  const socialLinks = [
    {
      icon: <FaLinkedin />,
      url: 'https://www.linkedin.com/in/omprakashutaha-b1815136/',
      label: 'LinkedIn'
    },
    {
      icon: <FaGithub />,
      url: 'https://github.com/OP3690',
      label: 'GitHub'
    },
    {
      icon: <FaFacebook />,
      url: 'https://www.facebook.com/global5665/',
      label: 'Facebook'
    },
    {
      icon: <FaInstagram />,
      url: 'https://www.instagram.com/omprakashutaha/',
      label: 'Instagram'
    }
  ];

  return (
    <ContactContainer id="contact">
      <Container>
        <SectionHeader
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <SectionTitle>Get In Touch</SectionTitle>
          <SectionSubtitle>
            Let's collaborate on your next project and turn your ideas into intelligent solutions
          </SectionSubtitle>
        </SectionHeader>

        <ContactGrid>
          <ContactInfo
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <ContactTitle>Contact Information</ContactTitle>

            {contactInfo.map((info, index) => (
              <ContactItem key={index}>
                <ContactIcon>{info.icon}</ContactIcon>
                <ContactText>
                  <ContactLabel>{info.label}</ContactLabel>
                  {info.link ? (
                    <ContactValue as="a" href={info.link} style={{ textDecoration: 'none', color: 'inherit' }}>
                      {info.value}
                    </ContactValue>
                  ) : (
                    <ContactValue>{info.value}</ContactValue>
                  )}
                </ContactText>
              </ContactItem>
            ))}

            <SocialLinks>
              {socialLinks.map((social, index) => (
                <SocialLink
                  key={index}
                  href={social.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  aria-label={social.label}
                  initial={{ opacity: 0, scale: 0 }}
                  animate={inView ? { opacity: 1, scale: 1 } : {}}
                  transition={{ duration: 0.5, delay: 0.4 + index * 0.1 }}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {social.icon}
                </SocialLink>
              ))}
            </SocialLinks>
          </ContactInfo>

          <ContactForm
            onSubmit={handleSubmit}
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            <FormTitle>Send Me a Message</FormTitle>

            {message && (
              <Message
                type={message.type}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
              >
                {message.text}
              </Message>
            )}

            <FormGroup>
              <Label htmlFor="name">Name *</Label>
              <Input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Your name"
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="email">Email *</Label>
              <Input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="your.email@example.com"
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="subject">Subject *</Label>
              <Input
                type="text"
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleInputChange}
                placeholder="What's this about?"
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="message">Message *</Label>
              <TextArea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleInputChange}
                placeholder="Tell me about your project or opportunity..."
                required
              />
            </FormGroup>

            <SubmitButton
              type="submit"
              disabled={isSubmitting}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              {isSubmitting ? 'Sending...' : 'Send Message'}
            </SubmitButton>
          </ContactForm>
        </ContactGrid>
      </Container>
    </ContactContainer>
  );
};

export default Contact;
