import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaHandshake, FaRocket, FaGithub, FaLinkedin } from 'react-icons/fa';

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: ${props => props.theme.colors.text.white};
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(120, 219, 255, 0.2) 0%, transparent 50%);
    z-index: 1;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  width: 100%;
  position: relative;
  z-index: 2;
  padding: 0 ${props => props.theme.spacing.lg};
`;

const HeroContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: 60px;
    text-align: center;
  }
`;

const LeftColumn = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RightColumn = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 30px;
`;

const ProfileImageContainer = styled(motion.div)`
  position: relative;
  width: 400px;
  height: 400px;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    width: 300px;
    height: 300px;
  }

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    width: 250px;
    height: 250px;
  }
`;

const ProfileImage = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  position: relative;
  background: linear-gradient(45deg, #667eea, #764ba2);
  padding: 8px;
  box-shadow: 
    0 30px 60px rgba(0, 0, 0, 0.3),
    0 0 0 1px rgba(255, 255, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -4px;
    left: -4px;
    right: -4px;
    bottom: -4px;
    background: linear-gradient(45deg, #667eea, #764ba2, #f093fb);
    border-radius: 50%;
    z-index: -1;
    animation: rotate 4s linear infinite;
  }

  @keyframes rotate {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const ProfileImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  border: 6px solid white;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
`;

const FloatingElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  width: 20px;
  height: 20px;
  background: linear-gradient(45deg, #f093fb, #f5576c);
  border-radius: 50%;
  opacity: 0.6;

  &:nth-child(1) {
    top: 10%;
    left: 10%;
    width: 15px;
    height: 15px;
  }

  &:nth-child(2) {
    top: 20%;
    right: 15%;
    width: 25px;
    height: 25px;
  }

  &:nth-child(3) {
    bottom: 15%;
    left: 20%;
    width: 18px;
    height: 18px;
  }

  &:nth-child(4) {
    bottom: 25%;
    right: 10%;
    width: 12px;
    height: 12px;
  }
`;

const MainTitle = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: white;
  line-height: 1.1;
  letter-spacing: -0.02em;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);

  span {
    background: linear-gradient(45deg, #f093fb, #f5576c);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
`;

const Description = styled(motion.p)`
  font-size: clamp(1.1rem, 2.5vw, 1.3rem);
  color: rgba(255, 255, 255, 0.95);
  line-height: 1.7;
  font-weight: ${props => props.theme.fonts.weights.medium};
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  gap: 15px;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    justify-content: center;
  }
`;

const SocialButton = styled(motion.a)`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  text-decoration: none;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-3px) scale(1.1);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 20px;
  flex-wrap: wrap;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    justify-content: center;
  }
`;

const Button = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 16px 32px;
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.base};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-decoration: none;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
  }

  &:hover::before {
    left: 100%;
  }

  ${props => props.variant === 'primary' && `
    background: linear-gradient(45deg, #f093fb, #f5576c);
    color: white;
    border: none;
    box-shadow: 0 10px 30px rgba(240, 147, 251, 0.4);

    &:hover {
      transform: translateY(-3px);
      box-shadow: 0 15px 40px rgba(240, 147, 251, 0.6);
    }
  `}

  ${props => props.variant === 'secondary' && `
    background: rgba(255, 255, 255, 0.1);
    color: white;
    border: 2px solid rgba(255, 255, 255, 0.3);
    backdrop-filter: blur(10px);

    &:hover {
      background: rgba(255, 255, 255, 0.2);
      border-color: rgba(255, 255, 255, 0.5);
      transform: translateY(-3px);
      box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
    }
  `}
`;

const StatsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-top: 20px;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    justify-content: center;
  }
`;

const StatCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  padding: 20px;
  border-radius: 20px;
  text-align: center;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(90deg, #f093fb, #f5576c);
  }

  &:hover {
    transform: translateY(-5px);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
  }
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  background: linear-gradient(45deg, #f093fb, #f5576c);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 5px;
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: rgba(255, 255, 255, 0.9);
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const ScrollIndicator = styled(motion.div)`
  position: absolute;
  bottom: 30px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  color: ${props => props.theme.colors.text.white};
  cursor: pointer;
  opacity: 0.7;

  &:hover {
    opacity: 1;
  }
`;

const ScrollText = styled.span`
  font-size: ${props => props.theme.fonts.sizes.sm};
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const ScrollIcon = styled(motion.div)`
  font-size: 20px;
  color: #f093fb;
`;

const Hero = () => {
  const [personalData, setPersonalData] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  useEffect(() => {
    const fetchPersonalData = async () => {
      try {
        const response = await fetch('/api/portfolio/personal');
        if (response.ok) {
          const data = await response.json();
          setPersonalData(data);
        }
      } catch (error) {
        console.error('Error fetching personal data:', error);
        // Fallback data
        setPersonalData({
          name: "Omprakash Utaha",
          title: "Senior Product Manager",
          location: "Bengaluru, Karnataka, India",
          summary: "Product | Fintech Enthusiast | Consumer Lending | CX",
          profileImage: "/Profile_Pic/OP.jpg"
        });
      }
    };

    fetchPersonalData();
  }, []);

  const scrollToNext = () => {
    const aboutSection = document.getElementById('about');
    if (aboutSection) {
      aboutSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  if (!personalData) {
    return <div>Loading...</div>;
  }

  return (
    <HeroContainer id="hero">
      <Container ref={ref}>
        <HeroContent>
          <LeftColumn
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 1, type: "spring", stiffness: 100 }}
          >
            <ProfileImageContainer>
              <ProfileImage>
                <ProfileImg src="/Profile_Pic/OP.jpg" alt="Omprakash Utaha" />
              </ProfileImage>
              <FloatingElements>
                <FloatingElement
                  animate={{ 
                    y: [0, -20, 0],
                    rotate: [0, 180, 360]
                  }}
                  transition={{ 
                    duration: 3, 
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
                <FloatingElement
                  animate={{ 
                    y: [0, 15, 0],
                    scale: [1, 1.2, 1]
                  }}
                  transition={{ 
                    duration: 4, 
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 1
                  }}
                />
                <FloatingElement
                  animate={{ 
                    x: [0, 10, 0],
                    rotate: [0, -180, -360]
                  }}
                  transition={{ 
                    duration: 3.5, 
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 0.5
                  }}
                />
                <FloatingElement
                  animate={{ 
                    y: [0, -10, 0],
                    scale: [1, 0.8, 1]
                  }}
                  transition={{ 
                    duration: 2.5, 
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 1.5
                  }}
                />
              </FloatingElements>
            </ProfileImageContainer>
          </LeftColumn>

          <RightColumn
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 1, type: "spring", stiffness: 100, delay: 0.2 }}
          >
            <MainTitle>
              Hello World, I'm <span>{personalData.name}</span>
            </MainTitle>

            <Description>
              I'm a fintech product leader who thrives on solving complex problems with data and first principles. With a decade of experience scaling products to millions, I'm passionate about building tech that transforms lives. When I'm not crafting solutions, I'm exploring new tech trends or inspiring communities through education.
            </Description>

            <SocialLinks
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: 0.5 }}
            >
              <SocialButton
                href="https://github.com/OP3690"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaGithub />
              </SocialButton>
              <SocialButton
                href="https://www.linkedin.com/in/omprakash-utaha-b181513"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaLinkedin />
              </SocialButton>
            </SocialLinks>

            <ActionButtons
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.6 }}
            >
              <Button
                href="#about"
                variant="primary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaRocket />
                Discover My Journey
              </Button>
              <Button
                href="#contact"
                variant="secondary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaHandshake />
                Connect With Me
              </Button>
            </ActionButtons>

            <StatsGrid
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.8 }}
            >
              <StatCard>
                <StatNumber>10+</StatNumber>
                <StatLabel>Years Experience</StatLabel>
              </StatCard>
              <StatCard>
                <StatNumber>$100M</StatNumber>
                <StatLabel>Revenue Growth</StatLabel>
              </StatCard>
              <StatCard>
                <StatNumber>20+</StatNumber>
                <StatLabel>Partners Onboarded</StatLabel>
              </StatCard>
              <StatCard>
                <StatNumber>∞</StatNumber>
                <StatLabel>Innovation Drive</StatLabel>
              </StatCard>
            </StatsGrid>
          </RightColumn>
        </HeroContent>
      </Container>

      <ScrollIndicator
        onClick={scrollToNext}
        initial={{ opacity: 0, y: 20 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.8, delay: 1 }}
        whileHover={{ y: 5 }}
      >
        <ScrollText>Scroll to explore</ScrollText>
        <ScrollIcon
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          ↓
        </ScrollIcon>
      </ScrollIndicator>
    </HeroContainer>
  );
};

export default Hero;
