import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaChartLine, FaPaperPlane, FaArrowDown } from 'react-icons/fa';

const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  position: relative;
  padding: 120px 0 80px;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: 2rem;
  }
`;

const HeroContent = styled.div`
  z-index: 2;
`;

const Badge = styled(motion.div)`
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  background: rgba(0, 212, 255, 0.1);
  border: 1px solid rgba(0, 212, 255, 0.3);
  padding: 0.5rem 1rem;
  border-radius: 50px;
  margin-bottom: 2rem;
  font-size: 0.9rem;
  font-weight: 500;
  color: ${({ theme }) => theme.colors.primary};
  backdrop-filter: blur(10px);
`;

const Title = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  margin-bottom: 1.5rem;
  line-height: 1.1;
  letter-spacing: -0.02em;
`;

const TitleLine = styled.span`
  display: block;
  background: ${({ highlight, theme }) => 
    highlight 
      ? `linear-gradient(135deg, ${theme.colors.primary} 0%, ${theme.colors.secondary} 100%)`
      : `linear-gradient(135deg, ${theme.colors.text} 0%, ${theme.colors.primary} 100%)`
  };
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Description = styled(motion.p)`
  font-size: 1.1rem;
  color: ${({ theme }) => theme.colors.textSecondary};
  margin-bottom: 2rem;
  line-height: 1.8;
  max-width: 600px;
`;

const Metrics = styled(motion.div)`
  display: flex;
  gap: 2rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    justify-content: center;
  }

  @media (max-width: 480px) {
    flex-direction: column;
    gap: 1rem;
  }
`;

const Metric = styled.div`
  text-align: center;
`;

const MetricNumber = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  font-family: ${({ theme }) => theme.fonts.mono};
  margin-bottom: 0.5rem;
`;

const MetricLabel = styled.div`
  font-size: 0.9rem;
  color: ${({ theme }) => theme.colors.textMuted};
  font-weight: 500;
`;

const Actions = styled(motion.div)`
  display: flex;
  gap: 1rem;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 12px 24px;
  border-radius: 8px;
  font-weight: 600;
  font-size: 0.95rem;
  text-decoration: none;
  transition: all 0.3s ease;
  cursor: pointer;

  &.primary {
    background: linear-gradient(135deg, ${({ theme }) => theme.colors.primary} 0%, ${({ theme }) => theme.colors.secondary} 100%);
    color: ${({ theme }) => theme.colors.white};
    box-shadow: 0 4px 24px 0 rgba(0, 212, 255, 0.3);

    &:hover {
      transform: translateY(-2px);
      box-shadow: 0 10px 25px rgba(0, 212, 255, 0.4);
    }
  }

  &.outline {
    background: transparent;
    color: ${({ theme }) => theme.colors.primary};
    border: 2px solid ${({ theme }) => theme.colors.primary};
    box-shadow: 0 2px 12px 0 rgba(0, 212, 255, 0.2);

    &:hover {
      background: ${({ theme }) => theme.colors.primary};
      color: ${({ theme }) => theme.colors.background};
      transform: translateY(-2px);
    }
  }
`;

const ProfileCard = styled(motion.div)`
  background: ${({ theme }) => theme.colors.surface};
  border-radius: 20px;
  padding: 2rem;
  backdrop-filter: blur(20px);
  border: 1px solid ${({ theme }) => theme.colors.surfaceLight};
  text-align: center;
  box-shadow: ${({ theme }) => theme.shadows.xl};
  position: relative;
  z-index: 2;
`;

const ProfileImage = styled.div`
  width: 200px;
  height: 200px;
  margin: 0 auto 1.5rem;
  border-radius: 50%;
  overflow: hidden;
  border: 4px solid ${({ theme }) => theme.colors.primary};
  box-shadow: 0 10px 30px rgba(0, 212, 255, 0.3);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  @media (max-width: 768px) {
    width: 150px;
    height: 150px;
  }
`;

const ProfileInfo = styled.div`
  h3 {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
    color: ${({ theme }) => theme.colors.text};
  }

  p {
    color: ${({ theme }) => theme.colors.primary};
    font-weight: 500;
    margin-bottom: 1.5rem;
  }
`;

const ProfileStats = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
`;

const Stat = styled.div`
  text-align: center;
`;

const StatValue = styled.div`
  font-size: 1.5rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  font-family: ${({ theme }) => theme.fonts.mono};
`;

const StatLabel = styled.div`
  font-size: 0.8rem;
  color: ${({ theme }) => theme.colors.textMuted};
  font-weight: 500;
`;

const Hero = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const [portfolioData, setPortfolioData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/portfolio/personal');
        const data = await response.json();
        setPortfolioData(data);
      } catch (error) {
        console.error('Error fetching portfolio data:', error);
        // Fallback data
        setPortfolioData({
          name: "Omprakash Utaha",
          title: "Senior Product Manager | Fintech Leader",
          summary: "Transforming financial services through innovative product solutions with 10+ years of experience driving digital transformation in banking and fintech."
        });
      }
    };

    fetchData();
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  const metrics = [
    { number: "₹10K+ Cr", label: "Loan Volume" },
    { number: "72%", label: "Efficiency Gain" },
    { number: "10+", label: "Banks & NBFCs" }
  ];

  return (
    <HeroSection>
      <Container ref={ref}>
        <HeroContent>
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate={inView ? "visible" : "hidden"}
          >
            <Badge variants={itemVariants}>
              <FaChartLine />
              <span>Fintech Product Leader</span>
            </Badge>

            <Title variants={itemVariants}>
              <TitleLine>Transforming</TitleLine>
              <TitleLine highlight>Financial Services</TitleLine>
              <TitleLine>Through Innovation</TitleLine>
            </Title>

            <Description variants={itemVariants}>
              {portfolioData?.summary || "Senior Product Manager with 10+ years of experience driving digital transformation in banking and fintech. Led ₹10,000+ Cr in loan disbursements with 72% efficiency gains."}
            </Description>

            <Metrics variants={itemVariants}>
              {metrics.map((metric, index) => (
                <Metric key={index}>
                  <MetricNumber>{metric.number}</MetricNumber>
                  <MetricLabel>{metric.label}</MetricLabel>
                </Metric>
              ))}
            </Metrics>

            <Actions variants={itemVariants}>
              <Button 
                href="#contact" 
                className="primary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaPaperPlane />
                Get In Touch
              </Button>
              <Button 
                href="#experience" 
                className="outline"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaArrowDown />
                View Journey
              </Button>
            </Actions>
          </motion.div>
        </HeroContent>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={inView ? { opacity: 1, x: 0 } : { opacity: 0, x: 50 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          <ProfileCard
            whileHover={{ y: -10 }}
            transition={{ duration: 0.3 }}
          >
            <ProfileImage>
              <img src="/images/profile.jpg" alt="Omprakash Utaha" />
            </ProfileImage>
            <ProfileInfo>
              <h3>{portfolioData?.name || "Omprakash Utaha"}</h3>
              <p>{portfolioData?.title || "Senior Product Manager"}</p>
              <ProfileStats>
                <Stat>
                  <StatValue>10+</StatValue>
                  <StatLabel>Years</StatLabel>
                </Stat>
                <Stat>
                  <StatValue>5</StatValue>
                  <StatLabel>Promotions</StatLabel>
                </Stat>
              </ProfileStats>
            </ProfileInfo>
          </ProfileCard>
        </motion.div>
      </Container>
    </HeroSection>
  );
};

export default Hero;
