import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaHandshake, FaRocket, FaGithub, FaLinkedin, FaArrowDown } from 'react-icons/fa';

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #1e3a8a 0%, #3730a3 50%, #7c3aed 100%);
  color: white;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, rgba(124, 58, 237, 0.4) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(59, 130, 246, 0.4) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(168, 85, 247, 0.3) 0%, transparent 50%);
    z-index: 1;
  }
`;

const Container = styled.div`
  max-width: 1400px;
  width: 100%;
  position: relative;
  z-index: 2;
  padding: 0 2rem;
`;

const HeroContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  min-height: 80vh;

  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 3rem;
    text-align: center;
    min-height: auto;
    padding: 4rem 0;
  }
`;

const LeftColumn = styled(motion.div)`
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 2rem;
`;

const RightColumn = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`;

const ProfileSection = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const ProfileImageContainer = styled(motion.div)`
  position: relative;
  width: 400px;
  height: 400px;

  @media (max-width: 1024px) {
    width: 300px;
    height: 300px;
  }

  @media (max-width: 768px) {
    width: 250px;
    height: 250px;
  }
`;

const ProfileImage = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  position: relative;
  background: linear-gradient(45deg, #8b5cf6, #3b82f6, #06b6d4);
  padding: 6px;
  box-shadow: 
    0 25px 50px rgba(0, 0, 0, 0.3),
    0 0 0 1px rgba(255, 255, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -3px;
    left: -3px;
    right: -3px;
    bottom: -3px;
    background: linear-gradient(45deg, #8b5cf6, #3b82f6, #06b6d4, #8b5cf6);
    border-radius: 50%;
    z-index: -1;
    animation: rotate 6s linear infinite;
  }

  @keyframes rotate {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const ProfileImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  border: 4px solid white;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
`;

const FloatingElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  width: 12px;
  height: 12px;
  background: linear-gradient(45deg, #8b5cf6, #3b82f6);
  border-radius: 50%;
  opacity: 0.8;

  &:nth-child(1) {
    top: 15%;
    left: 10%;
    width: 8px;
    height: 8px;
  }

  &:nth-child(2) {
    top: 25%;
    right: 15%;
    width: 16px;
    height: 16px;
  }

  &:nth-child(3) {
    bottom: 20%;
    left: 20%;
    width: 10px;
    height: 10px;
  }

  &:nth-child(4) {
    bottom: 30%;
    right: 10%;
    width: 14px;
    height: 14px;
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1.5rem;
  margin-top: 2rem;

  @media (max-width: 1024px) {
    justify-content: center;
    max-width: 400px;
    margin: 2rem auto 0;
  }
`;

const StatCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  padding: 1.5rem;
  border-radius: 20px;
  text-align: center;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(90deg, #8b5cf6, #3b82f6);
  }

  &:hover {
    transform: translateY(-5px);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
  }
`;

const StatNumber = styled.div`
  font-size: 2rem;
  font-weight: 700;
  background: linear-gradient(45deg, #8b5cf6, #3b82f6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  font-size: 0.875rem;
  color: rgba(255, 255, 255, 0.9);
  font-weight: 500;
`;

const ContentSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  max-width: 600px;
`;

const Greeting = styled(motion.div)`
  font-size: 1.125rem;
  color: rgba(255, 255, 255, 0.8);
  font-weight: 500;
  margin-bottom: 0.5rem;
`;

const MainTitle = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 700;
  color: white;
  line-height: 1.1;
  letter-spacing: -0.02em;
  margin-bottom: 1.5rem;

  span {
    background: linear-gradient(45deg, #8b5cf6, #3b82f6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
`;

const Description = styled(motion.p)`
  font-size: clamp(1.1rem, 2.5vw, 1.25rem);
  color: rgba(255, 255, 255, 0.9);
  line-height: 1.7;
  font-weight: 400;
  margin-bottom: 2rem;
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;

  @media (max-width: 1024px) {
    justify-content: center;
  }
`;

const SocialButton = styled(motion.a)`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  text-decoration: none;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  font-size: 1.25rem;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-3px) scale(1.1);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 1.5rem;
  flex-wrap: wrap;

  @media (max-width: 1024px) {
    justify-content: center;
  }

  @media (max-width: 640px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem 2rem;
  border-radius: 50px;
  font-size: 1rem;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  position: relative;
  overflow: hidden;
  min-width: 180px;
  justify-content: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
  }

  &:hover::before {
    left: 100%;
  }

  ${props => props.variant === 'primary' && `
    background: linear-gradient(45deg, #8b5cf6, #3b82f6);
    color: white;
    border: none;
    box-shadow: 0 10px 30px rgba(139, 92, 246, 0.4);

    &:hover {
      transform: translateY(-3px);
      box-shadow: 0 15px 40px rgba(139, 92, 246, 0.6);
    }
  `}

  ${props => props.variant === 'secondary' && `
    background: rgba(255, 255, 255, 0.1);
    color: white;
    border: 2px solid rgba(255, 255, 255, 0.3);
    backdrop-filter: blur(10px);

    &:hover {
      background: rgba(255, 255, 255, 0.2);
      border-color: rgba(255, 255, 255, 0.5);
      transform: translateY(-3px);
      box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
    }
  `}
`;

const ScrollIndicator = styled(motion.div)`
  position: absolute;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.7);
  cursor: pointer;

  &:hover {
    color: white;
  }
`;

const ScrollText = styled.span`
  font-size: 0.875rem;
  font-weight: 500;
`;

const ScrollIcon = styled(motion.div)`
  font-size: 1.5rem;
  color: #8b5cf6;
`;

const Hero = () => {
  const [personalData, setPersonalData] = useState({ name: "Omprakash Utaha", profileImage: "/OP.jpg" });
  const { ref, inView } = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  useEffect(() => {
    const fetchPersonalData = async () => {
      try {
        const response = await fetch('/api/portfolio/personal');
        if (response.ok) {
          const data = await response.json();
          setPersonalData({
            name: "Omprakash Utaha",
            title: "Senior Product Manager",
            location: "Bengaluru, Karnataka, India",
            summary: "Product | Fintech Enthusiast | Consumer Lending | CX",
            profileImage: "/OP.jpg"
          });
        }
      } catch (error) {
        console.error('Error fetching personal data:', error);
        setPersonalData({ name: "Omprakash Utaha", profileImage: "/OP.jpg" });
      }
    };
    fetchPersonalData();
  }, []);

  const scrollToNext = () => {
    const aboutSection = document.getElementById('about');
    if (aboutSection) {
      aboutSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer id="hero">
      <Container ref={ref}>
        <HeroContent>
          <LeftColumn
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 1, type: "spring", stiffness: 100 }}
          >
            <ContentSection>
              <Greeting
                initial={{ opacity: 0, y: 20 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.2 }}
              >
                Hello World, I'm
              </Greeting>

              <MainTitle
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.4 }}
              >
                <span>{personalData.name}</span>
              </MainTitle>

              <Description
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.6 }}
              >
                I'm a fintech product leader who thrives on solving complex problems with data and first principles. With a decade of experience scaling products to millions, I'm passionate about building tech that transforms lives.
              </Description>

              <SocialLinks
                initial={{ opacity: 0, y: 20 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.8 }}
              >
                <SocialButton
                  href="https://github.com/OP3690"
                  target="_blank"
                  rel="noopener noreferrer"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  <FaGithub />
                </SocialButton>
                <SocialButton
                  href="https://www.linkedin.com/in/omprakash-utaha-b181513"
                  target="_blank"
                  rel="noopener noreferrer"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  <FaLinkedin />
                </SocialButton>
              </SocialLinks>

              <ActionButtons
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 1 }}
              >
                <Button
                  href="#about"
                  variant="primary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <FaRocket />
                  Discover My Journey
                </Button>
                <Button
                  href="#contact"
                  variant="secondary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <FaHandshake />
                  Connect With Me
                </Button>
              </ActionButtons>
            </ContentSection>
          </LeftColumn>

          <RightColumn
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 1, type: "spring", stiffness: 100, delay: 0.2 }}
          >
            <ProfileSection>
              <ProfileImageContainer>
                <ProfileImage>
                  <ProfileImg src="/OP.jpg" alt="Omprakash Utaha" />
                </ProfileImage>
                <FloatingElements>
                  <FloatingElement
                    animate={{ 
                      y: [0, -20, 0],
                      rotate: [0, 180, 360]
                    }}
                    transition={{ 
                      duration: 4, 
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                  <FloatingElement
                    animate={{ 
                      y: [0, 15, 0],
                      scale: [1, 1.2, 1]
                    }}
                    transition={{ 
                      duration: 5, 
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 1
                    }}
                  />
                  <FloatingElement
                    animate={{ 
                      x: [0, 10, 0],
                      rotate: [0, -180, -360]
                    }}
                    transition={{ 
                      duration: 4.5, 
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 0.5
                    }}
                  />
                  <FloatingElement
                    animate={{ 
                      y: [0, -10, 0],
                      scale: [1, 0.8, 1]
                    }}
                    transition={{ 
                      duration: 3.5, 
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 1.5
                    }}
                  />
                </FloatingElements>
              </ProfileImageContainer>

              <StatsGrid
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 1.2 }}
              >
                <StatCard
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.2 }}
                >
                  <StatNumber>10+</StatNumber>
                  <StatLabel>Years Experience</StatLabel>
                </StatCard>
                <StatCard
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.2 }}
                >
                  <StatNumber>$100M</StatNumber>
                  <StatLabel>Revenue Growth</StatLabel>
                </StatCard>
                <StatCard
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.2 }}
                >
                  <StatNumber>20+</StatNumber>
                  <StatLabel>Partners Onboarded</StatLabel>
                </StatCard>
                <StatCard
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.2 }}
                >
                  <StatNumber>âˆž</StatNumber>
                  <StatLabel>Innovation Drive</StatLabel>
                </StatCard>
              </StatsGrid>
            </ProfileSection>
          </RightColumn>
        </HeroContent>
      </Container>

      <ScrollIndicator
        onClick={scrollToNext}
        initial={{ opacity: 0, y: 20 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.8, delay: 1.4 }}
        whileHover={{ y: 5 }}
      >
        <ScrollText>Scroll to explore</ScrollText>
        <ScrollIcon
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <FaArrowDown />
        </ScrollIcon>
      </ScrollIndicator>
    </HeroContainer>
  );
};

export default Hero;
