import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';

const ExperienceContainer = styled.section`
  padding: 80px 0;
  background: linear-gradient(135deg, ${props => props.theme.colors.background.primary} 0%, ${props => props.theme.colors.background.secondary} 100%);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled(motion.h2)`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  text-align: center;
  margin-bottom: 60px;
  color: ${props => props.theme.colors.text.primary};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 4px;
    background: linear-gradient(90deg, ${props => props.theme.colors.primary} 0%, ${props => props.theme.colors.secondary} 100%);
    border-radius: 2px;
  }
`;

const TimelineContainer = styled.div`
  position: relative;
  max-width: 800px;
  margin: 0 auto;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 4px;
    background: linear-gradient(180deg, ${props => props.theme.colors.primary} 0%, ${props => props.theme.colors.secondary} 100%);
    transform: translateX(-50%);

    @media (max-width: 768px) {
      left: 20px;
    }
  }
`;

const ExperienceCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: 30px;
  margin: 30px 0;
  position: relative;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    width: 20px;
    height: 20px;
    background: ${props => props.theme.colors.primary};
    border-radius: 50%;
    transform: translateY(-50%);

    @media (min-width: 769px) {
      ${props => props.align === 'left' ? 'right: -60px;' : 'left: -60px;'}
    }

    @media (max-width: 768px) {
      left: -40px;
    }
  }

  @media (min-width: 769px) {
    width: 45%;
    ${props => props.align === 'left' ? 'margin-right: 55%;' : 'margin-left: 55%;'}
  }

  @media (max-width: 768px) {
    margin-left: 40px;
  }
`;

const CompanyLogo = styled.img`
  width: 60px;
  height: 60px;
  object-fit: contain;
  border-radius: ${props => props.theme.borderRadius.md};
  margin-bottom: 15px;
  background: white;
  padding: 5px;
`;

const JobTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.lg};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.theme.colors.text.primary};
  margin-bottom: 8px;
`;

const CompanyName = styled.h4`
  font-size: ${props => props.theme.fonts.sizes.md};
  font-weight: ${props => props.theme.fonts.weights.medium};
  color: ${props => props.theme.colors.primary};
  margin-bottom: 10px;
`;

const JobMeta = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 15px;
  flex-wrap: wrap;
`;

const MetaItem = styled.div`
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.theme.colors.text.secondary};
`;

const Description = styled.p`
  font-size: ${props => props.theme.fonts.sizes.base};
  line-height: 1.6;
  color: ${props => props.theme.colors.text.secondary};
  margin-bottom: 15px;
`;

const SkillsList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 15px;
`;

const SkillTag = styled.span`
  background: rgba(14, 165, 233, 0.1);
  color: ${props => props.theme.colors.primary};
  padding: 4px 12px;
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.sm};
  border: 1px solid rgba(14, 165, 233, 0.2);
`;

const CareerProgressionCard = styled(motion.div)`
  background: linear-gradient(135deg, rgba(14, 165, 233, 0.1) 0%, rgba(59, 130, 246, 0.1) 100%);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(14, 165, 233, 0.3);
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: 40px;
