import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaCalendarAlt, FaMapMarkerAlt, FaExternalLinkAlt } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const ExperienceSection = styled.section`
  padding: ${props => props.theme.spacing.section} 0;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.03) 0%, rgba(255, 255, 255, 0.01) 100%);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 80% 80%, rgba(102, 126, 234, 0.08) 0%, transparent 50%),
                radial-gradient(circle at 20% 20%, rgba(118, 75, 162, 0.08) 0%, transparent 50%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.xl};
  position: relative;
  z-index: 2;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.lg};
  }
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xxxl};
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.lg};
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const SectionSubtitle = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.8;
  opacity: 0.9;
`;

const Timeline = styled.div`
  position: relative;
  max-width: 1000px;
  margin: 0 auto;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(180deg, #667eea 0%, #764ba2 100%);
    transform: translateX(-50%);
    opacity: 0.3;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      left: 30px;
    }
  }
`;

const TimelineItem = styled(motion.div)`
  position: relative;
  margin-bottom: ${props => props.theme.spacing.xxxl};
  display: flex;
  align-items: center;

  &:nth-child(odd) {
    flex-direction: row;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      flex-direction: row;
    }
  }

  &:nth-child(even) {
    flex-direction: row-reverse;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      flex-direction: row;
    }
  }
`;

const TimelineContent = styled.div`
  flex: 1;
  max-width: 450px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing.xl};
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  transition: all ${props => props.theme.transitions.normal};
  position: relative;

  &:hover {
    transform: translateY(-8px);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 50%;
    transform: translateY(-50%);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.4);
  }

  &:nth-child(odd) &::before {
    right: -60px;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      left: -60px;
      right: auto;
    }
  }

  &:nth-child(even) &::before {
    left: -60px;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      left: -60px;
    }
  }
`;

const CompanyLogo = styled.a`
  width: 120px;
  height: 60px;
  background: white;
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.sm};
  text-decoration: none;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: ${props => props.theme.spacing.md};
  transition: all ${props => props.theme.transitions.normal};
  position: relative;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);

    &::after {
      content: 'ðŸ”—';
      position: absolute;
      top: -8px;
      right: -8px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      width: 24px;
      height: 24px;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 12px;
      box-shadow: 0 2px 8px rgba(102, 126, 234, 0.4);
    }
  }

  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const JobTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const CompanyName = styled.h4`
  font-size: ${props => props.theme.fonts.sizes.lg};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const JobDetails = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.md};
  color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  font-size: ${props => props.theme.fonts.sizes.sm};
`;

const JobDescription = styled.p`
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  line-height: 1.7;
  margin-bottom: ${props => props.theme.spacing.md};
`;

const SkillsList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: ${props => props.theme.spacing.sm};
  margin-top: ${props => props.theme.spacing.md};
`;

const SkillTag = styled.span`
  background: rgba(102, 126, 234, 0.1);
  color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
  padding: ${props => props.theme.spacing.xs} ${props => props.theme.spacing.sm};
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.xs};
  font-weight: ${props => props.theme.fonts.weights.medium};
  border: 1px solid rgba(102, 126, 234, 0.2);
`;

const Experience = () => {
  const { isDarkMode } = useTheme();
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const experiences = [
    {
      id: 1,
      company: 'Lendingkart',
