import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaEnvelope, FaPhone, FaMapMarkerAlt, FaLinkedin, FaGithub, FaPaperPlane } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const ContactSection = styled.section`
  padding: ${props => props.theme.spacing.section} 0;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.primary : props.theme.colors.light.background.primary};
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${props => props.isDarkMode ? 
      'radial-gradient(circle at 30% 70%, rgba(56, 189, 248, 0.05) 0%, transparent 50%), radial-gradient(circle at 70% 30%, rgba(240, 171, 252, 0.05) 0%, transparent 50%)' :
      'radial-gradient(circle at 30% 70%, rgba(14, 165, 233, 0.05) 0%, transparent 50%), radial-gradient(circle at 70% 30%, rgba(217, 70, 239, 0.05) 0%, transparent 50%)'
    };
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.lg};
  position: relative;
  z-index: 1;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.md};
  }
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing['4xl']};
`;

const SectionTitle = styled.h2`
  font-size: ${props => props.theme.fonts.sizes['4xl']};
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.lg};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -${props => props.theme.spacing.md};
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: ${props => props.theme.gradients.primary};
    border-radius: ${props => props.theme.borderRadius.full};
  }

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    font-size: ${props => props.theme.fonts.sizes['3xl']};
  }
`;

const SectionSubtitle = styled.p`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.secondary : props.theme.colors.light.text.secondary};
  max-width: 700px;
  margin: 0 auto;
  line-height: ${props => props.theme.fonts.lineHeights.relaxed};
`;

const ContactGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing['4xl']};
  max-width: 1200px;
  margin: 0 auto;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing['3xl']};
  }
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.xl};
`;

const ContactCard = styled.div`
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing['2xl']};
  box-shadow: ${props => props.theme.shadows.glass};
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-4px);
    box-shadow: ${props => props.theme.shadows.xl};
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  }
`;

const ContactHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const ContactIcon = styled.div`
  width: 50px;
  height: 50px;
  background: ${props => props.theme.gradients.primary};
  border-radius: ${props => props.theme.borderRadius.lg};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${props => props.theme.fonts.sizes.xl};
  box-shadow: ${props => props.theme.shadows.md};
`;

const ContactTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
`;

const ContactValue = styled.a`
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  text-decoration: none;
  font-weight: ${props => props.theme.fonts.weights.medium};
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.highlight : props.theme.colors.light.text.highlight};
    transform: translateY(-1px);
  }
`;

const ContactForm = styled(motion.form)`
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing['2xl']};
  box-shadow: ${props => props.theme.shadows.glass};
`;

const FormTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes['2xl']};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.lg};
  text-align: center;
`;

const HelpSection = styled.div`
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.secondary : props.theme.colors.light.border.secondary};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.xl};
`;

const HelpTitle = styled.h4`
  font-size: ${props => props.theme.fonts.sizes.lg};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const HelpContent = styled.div`
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.secondary : props.theme.colors.light.text.secondary};
  line-height: ${props => props.theme.fonts.lineHeights.relaxed};
`;

const SocialLinks = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-top: ${props => props.theme.spacing.md};
`;

const SocialLink = styled(motion.a)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 45px;
  height: 45px;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.secondary : props.theme.colors.light.border.secondary};
  border-radius: ${props => props.theme.borderRadius.full};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.sm};

  &:hover {
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.md};
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  }
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.lg};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.sm};

  &:nth-child(3),
  &:nth-child(4) {
    grid-column: span 2;

    @media (max-width: ${props => props.theme.breakpoints.md}) {
      grid-column: span 1;
    }
  }
`;

const Label = styled.label`
  font-size: ${props => props.theme.fonts.sizes.sm};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
`;

const Input = styled.input`
  padding: ${props => props.theme.spacing.md};
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.secondary : props.theme.colors.light.border.secondary};
  border-radius: ${props => props.theme.borderRadius.lg};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  font-size: ${props => props.theme.fonts.sizes.base};
  transition: all ${props => props.theme.transitions.normal};

  &::placeholder {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.tertiary : props.theme.colors.light.text.tertiary};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    box-shadow: 0 0 0 3px ${props => props.isDarkMode ? 'rgba(56, 189, 248, 0.1)' : 'rgba(14, 165, 233, 0.1)'};
  }
`;

const TextArea = styled.textarea`
  padding: ${props => props.theme.spacing.md};
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.secondary : props.theme.colors.light.border.secondary};
  border-radius: ${props => props.theme.borderRadius.lg};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  font-size: ${props => props.theme.fonts.sizes.base};
  font-family: inherit;
  resize: vertical;
  min-height: 120px;
  transition: all ${props => props.theme.transitions.normal};

  &::placeholder {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.tertiary : props.theme.colors.light.text.tertiary};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    box-shadow: 0 0 0 3px ${props => props.isDarkMode ? 'rgba(56, 189, 248, 0.1)' : 'rgba(14, 165, 233, 0.1)'};
  }
`;

const SubmitButton = styled(motion.button)`
  width: 100%;
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.xl};
  background: ${props => props.theme.gradients.primary};
  color: white;
  border: none;
  border-radius: ${props => props.theme.borderRadius.lg};
  font-size: ${props => props.theme.fonts.sizes.base};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.md};
  display: flex;
  align-items: center;
  justify-content: center;
  gap: ${props => props.theme.spacing.sm};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.lg};
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Contact = () => {
  const { isDarkMode } = useTheme();
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: ''
      });
      alert('Thank you for your message! I will get back to you soon.');
    }, 2000);
  };

  return (
    <ContactSection id="contact" ref={ref}>
      <Container>
        <SectionHeader
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <SectionTitle>Get In Touch</SectionTitle>
          <SectionSubtitle>
            Ready to collaborate on your next fintech project? Let's discuss how we can work together 
            to build innovative products that scale from 0 to millions of users.
          </SectionSubtitle>
        </SectionHeader>

        <ContactGrid>
          <ContactInfo>
            <ContactCard
              initial={{ opacity: 0, x: -30 }}
              animate={inView ? { opacity: 1, x: 0 } : {}}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              <ContactHeader>
                <ContactIcon>
                  <FaEnvelope />
                </ContactIcon>
                <ContactTitle>Email</ContactTitle>
