const Skills = () => {
  const [skillsData, setSkillsData] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  useEffect(() => {
    const fetchSkills = async () => {
      try {
        const response = await fetch('/api/portfolio/skills');
        if (response.ok) {
          const data = await response.json();
          setSkillsData(data);
        }
      } catch (error) {
        console.error('Error fetching skills data:', error);
        // Fallback data
        setSkillsData({
          technical: [
            { name: "Python", level: 95 },
            { name: "Data Science", level: 92 },
            { name: "Machine Learning", level: 90 },
            { name: "Deep Learning", level: 85 },
            { name: "SQL & Database", level: 90 },
            { name: "Microsoft Excel", level: 95 }
          ],
          soft: [
            { name: "Leadership", level: 90 },
            { name: "Strategic Planning", level: 85 },
            { name: "Team Management", level: 88 },
            { name: "Communication", level: 92 },
            { name: "Problem Solving", level: 90 },
            { name: "Stakeholder Management", level: 85 }
          ],
          fintech: [
            { name: "Credit Underwriting", level: 95 },
            { name: "Digital Lending", level: 90 },
            { name: "Risk Management", level: 85 },
            { name: "Compliance", level: 80 },
            { name: "Payment Systems", level: 75 },
            { name: "Blockchain", level: 70 }
          ],
          tools: [
            { name: "JIRA", level: 90 },
            { name: "Confluence", level: 85 },
            { name: "Figma", level: 75 },
            { name: "Tableau", level: 80 },
            { name: "Postman", level: 70 },
            { name: "Git", level: 75 }
          ],
          certifications: [
            { name: "Becoming an AI-First Product Leader", issuer: "Coursera", date: "2024" },
            { name: "GPT-4 Foundations: Building AI-Powered Apps", issuer: "Coursera", date: "2024" },
            { name: "CCCA, BCC AND COMPUTER HARDWARE", issuer: "Technical Institute", date: "2013" },
            { name: "Metrics for Product Managers", issuer: "Product School", date: "2023" },
            { name: "Technology for Product Managers", issuer: "Product School", date: "2023" }
          ],
          languages: [
            { name: "English", level: "Full Professional" },
            { name: "Hindi", level: "Native or Bilingual" },
            { name: "Gujarati", level: "Native or Bilingual" }
          ]
        });
      }
    };

    fetchSkills();
  }, []);

  if (!skillsData) {
    return <div>Loading skills...</div>;
  }

  const skillCategories = [
    {
      title: "Core Skills",
      icon: <FaCode />,
      skills: skillsData.technical || []
    },
    {
      title: "Leadership & Soft Skills",
      icon: <FaUsers />,
      skills: skillsData.soft || []
    },
    {
      title: "Fintech & Banking",
      icon: <FaChartLine />,
      skills: skillsData.fintech || []
    },
    {
      title: "Tools & Platforms",
      icon: <FaTools />,
      skills: skillsData.tools || []
    }
  ];

  return (
    <SkillsContainer id="skills">
      <Container>
        <SectionHeader
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <SectionTitle>Skills & Technologies</SectionTitle>
          <SectionSubtitle>
            A comprehensive toolkit spanning the entire data science and AI development ecosystem
          </SectionSubtitle>
        </SectionHeader>

        <SkillsGrid>
          {skillCategories.map((category, index) => (
            <SkillCategory
              key={category.title}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.1 }}
            >
              <CategoryHeader>
                <CategoryIcon>{category.icon}</CategoryIcon>
                <CategoryTitle>{category.title}</CategoryTitle>
              </CategoryHeader>

              <SkillsList>
                {category.skills.map((skill, skillIndex) => (
                  <SkillItem key={skillIndex}>
                    <SkillName>{skill.name}</SkillName>
                    <SkillLevel>
                      <SkillBar>
                        <SkillProgress
                          initial={{ width: 0 }}
                          animate={inView ? { width: `${skill.level}%` } : {}}
                          transition={{ duration: 1, delay: 0.5 + skillIndex * 0.1 }}
                        />
                      </SkillBar>
                      <SkillPercentage>{skill.level}%</SkillPercentage>
                    </SkillLevel>
                  </SkillItem>
                ))}
              </SkillsList>
            </SkillCategory>
          ))}
        </SkillsGrid>

        {/* Certifications Section */}
        <CertificationsSection
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <CertificationsTitle>
            <FaGraduationCap />
            Certifications & Training
          </CertificationsTitle>

          <CertificationsGrid>
            {skillsData.certifications?.map((cert, index) => (
              <CertificationCard
                key={index}
                initial={{ opacity: 0, scale: 0.9 }}
                animate={inView ? { opacity: 1, scale: 1 } : {}}
                transition={{ duration: 0.5, delay: 0.6 + index * 0.1 }}
              >
                <CertificationName>{cert.name}</CertificationName>
                <CertificationIssuer>{cert.issuer}</CertificationIssuer>
                <CertificationDate>{cert.date}</CertificationDate>
              </CertificationCard>
            ))}
          </CertificationsGrid>
        </CertificationsSection>

        {/* Languages Section */}
        <LanguagesSection
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <LanguagesTitle>Languages</LanguagesTitle>

          <LanguagesGrid>
            {skillsData.languages?.map((language, index) => (
              <LanguageCard key={index}>
                <LanguageName>{language.name}</LanguageName>
                <LanguageLevel>{language.level}</LanguageLevel>
              </LanguageCard>
            ))}
          </LanguagesGrid>
        </LanguagesSection>
      </Container>
    </SkillsContainer>
  );
};

