import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaUser, FaRocket, FaLightbulb, FaUsers } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const AboutSection = styled.section`
  padding: ${props => props.theme.spacing.section} 0;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.05) 0%, rgba(255, 255, 255, 0.02) 100%);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 20% 80%, rgba(102, 126, 234, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 80% 20%, rgba(118, 75, 162, 0.1) 0%, transparent 50%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.xl};
  position: relative;
  z-index: 2;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.lg};
  }
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xxxl};
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.lg};
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const SectionSubtitle = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  line-height: 1.8;
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  max-width: 800px;
  margin: 0 auto;
  opacity: 0.9;
`;

const AboutContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.xxxl};
  align-items: center;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.xxl};
  }
`;

const AboutText = styled.div`
  color: ${props => props.isDarkMode ? '#e2e8f0' : '#334155'};
`;

const AboutDescription = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  line-height: 1.8;
  margin-bottom: ${props => props.theme.spacing.xl};
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
`;

const AboutStats = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: ${props => props.theme.spacing.lg};
  margin-top: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    grid-template-columns: 1fr;
  }
`;

const StatCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing.xl};
  text-align: center;
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-8px);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }
`;

const StatIcon = styled.div`
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: ${props => props.theme.borderRadius.full};
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto ${props => props.theme.spacing.md};
  color: white;
  font-size: ${props => props.theme.fonts.sizes.xl};
  box-shadow: 0 8px 24px rgba(102, 126, 234, 0.3);
  transition: all ${props => props.theme.transitions.normal};

  ${StatCard}:hover & {
    transform: scale(1.1);
    box-shadow: 0 12px 32px rgba(102, 126, 234, 0.4);
  }
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.xs};
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const AboutVisual = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const GlassCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(25px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.xxl};
  padding: ${props => props.theme.spacing.xxl};
  max-width: 500px;
  width: 100%;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.1);
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
