import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaArrowRight, FaHandshake, FaRocket } from 'react-icons/fa';

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => props.theme.colors.background.hero};
  color: ${props => props.theme.colors.text.white};
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(30, 64, 175, 0.9) 0%, rgba(20, 184, 166, 0.1) 100%);
    z-index: 1;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  width: 100%;
  text-align: center;
  position: relative;
  z-index: 2;
  padding: 0 ${props => props.theme.spacing.lg};
`;

const Greeting = styled(motion.div)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.theme.colors.accent};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  margin-bottom: 20px;
  opacity: 0.9;
`;

const MainTitle = styled(motion.h1)`
  font-size: clamp(3rem, 8vw, 5rem);
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.theme.colors.text.white};
  line-height: 1.1;
  margin-bottom: 20px;
  letter-spacing: -0.02em;
`;

const Subtitle = styled(motion.p)`
  font-size: clamp(1.25rem, 4vw, 1.75rem);
  color: ${props => props.theme.colors.text.white};
  line-height: 1.4;
  margin-bottom: 30px;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
  opacity: 0.9;
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const Description = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.theme.colors.text.white};
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
  opacity: 0.85;
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 60px;
`;

const Button = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 16px 32px;
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.base};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  cursor: pointer;
  box-shadow: ${props => props.theme.shadows.md};

  ${props => props.variant === 'primary' && `
    background: ${props.theme.colors.accent};
    color: white;
    border: none;

    &:hover {
      transform: translateY(-2px);
      box-shadow: ${props.theme.shadows.lg};
      background: ${props.theme.colors.accent}dd;
    }
  `}

  ${props => props.variant === 'secondary' && `
    background: transparent;
    color: ${props.theme.colors.text.white};
    border: 2px solid ${props.theme.colors.text.white};

    &:hover {
      background: ${props.theme.colors.text.white};
      color: ${props.theme.colors.background.hero};
      transform: translateY(-2px);
      box-shadow: ${props.theme.shadows.lg};
    }
  `}
`;

const StatsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 30px;
  max-width: 800px;
  margin: 0 auto;
`;

const StatCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  padding: 30px 20px;
  border-radius: ${props => props.theme.borderRadius.xl};
  text-align: center;
  transition: transform ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-5px);
    background: rgba(255, 255, 255, 0.15);
  }
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxxl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.theme.colors.accent};
  margin-bottom: 8px;
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.theme.colors.text.white};
  font-weight: ${props => props.theme.fonts.weights.medium};
  opacity: 0.9;
`;

const ScrollIndicator = styled(motion.div)`
  position: absolute;
  bottom: 30px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  color: ${props => props.theme.colors.text.white};
  cursor: pointer;
  opacity: 0.7;

  &:hover {
    opacity: 1;
  }
`;

const ScrollText = styled.span`
  font-size: ${props => props.theme.fonts.sizes.sm};
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const ScrollIcon = styled(motion.div)`
  font-size: 20px;
  color: ${props => props.theme.colors.accent};
`;

const Hero = () => {
  const [personalData, setPersonalData] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  useEffect(() => {
    const fetchPersonalData = async () => {
      try {
        const response = await fetch('/api/portfolio/personal');
        if (response.ok) {
          const data = await response.json();
          setPersonalData(data);
        }
      } catch (error) {
        console.error('Error fetching personal data:', error);
        // Fallback data
        setPersonalData({
          name: "Omprakash Utaha",
          title: "Senior Product Manager",
          location: "Bengaluru, Karnataka, India",
          summary: "Product | Fintech Enthusiast | Consumer Lending | CX",
          profileImage: "/images/profile.jpg"
        });
      }
    };

    fetchPersonalData();
  }, []);

  const scrollToNext = () => {
    const aboutSection = document.getElementById('about');
    if (aboutSection) {
      aboutSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  if (!personalData) {
    return <div>Loading...</div>;
  }

  return (
    <HeroContainer id="hero">
      <Container ref={ref}>
        <Greeting
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          Hey, I'm {personalData.name}
        </Greeting>

        <MainTitle
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          A seasoned product guy who loves crafting solutions with data, first principles, and a passion for technology.
        </MainTitle>

        <Description
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          With over a decade in product management, I thrive on turning complex problems into scalable products that impact millions. From fintech to insurance, I've driven growth from zero to $100M. Outside work, you'll find me exploring new tech trends, traveling to new horizons, or contributing to community initiatives like education awareness. Let's build something extraordinary together!
        </Description>

        <ActionButtons
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <Button
            href="#about"
            variant="primary"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaRocket />
            Discover My Journey
          </Button>
          <Button
            href="#contact"
            variant="secondary"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaHandshake />
            Connect With Me
          </Button>
        </ActionButtons>

        <StatsGrid
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <StatCard>
            <StatNumber>10+</StatNumber>
            <StatLabel>Years Experience</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>$100M</StatNumber>
            <StatLabel>Revenue Growth</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>20+</StatNumber>
            <StatLabel>Partners Onboarded</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>∞</StatNumber>
            <StatLabel>Innovation Drive</StatLabel>
          </StatCard>
        </StatsGrid>
      </Container>

      <ScrollIndicator
        onClick={scrollToNext}
        initial={{ opacity: 0, y: 20 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.8, delay: 1 }}
        whileHover={{ y: 5 }}
      >
        <ScrollText>Scroll to explore</ScrollText>
        <ScrollIcon
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          ↓
        </ScrollIcon>
      </ScrollIndicator>
    </HeroContainer>
  );
};

export default Hero;
