import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaDownload, FaCoffee } from 'react-icons/fa';

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 120px 20px 80px;
  background: ${props => props.theme.colors.background.primary};
  position: relative;
`;

const Container = styled.div`
  max-width: 1200px;
  width: 100%;
  text-align: center;
`;

const Badge = styled(motion.div)`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: ${props => props.theme.colors.primary};
  color: white;
  padding: 8px 16px;
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.sm};
  font-weight: ${props => props.theme.fonts.weights.medium};
  margin-bottom: 30px;
  box-shadow: ${props => props.theme.shadows.md};
`;

const MainTitle = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.theme.colors.text.primary};
  line-height: 1.2;
  margin-bottom: 20px;

  .highlight {
    color: ${props => props.theme.colors.primary};
  }
`;

const Subtitle = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.theme.colors.text.secondary};
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 60px;
`;

const Button = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 10px;
  padding: 15px 30px;
  border-radius: ${props => props.theme.borderRadius.lg};
  font-size: ${props => props.theme.fonts.sizes.base};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  cursor: pointer;
  box-shadow: ${props => props.theme.shadows.md};

  ${props => props.variant === 'primary' && `
    background: ${props.theme.colors.primary};
    color: white;
    border: none;

    &:hover {
      transform: translateY(-2px);
      box-shadow: ${props.theme.shadows.lg};
    }
  `}

  ${props => props.variant === 'secondary' && `
    background: white;
    color: ${props.theme.colors.text.primary};
    border: 2px solid ${props.theme.colors.neutral[200]};

    &:hover {
      border-color: ${props.theme.colors.primary};
      transform: translateY(-2px);
      box-shadow: ${props.theme.shadows.lg};
    }
  `}
`;

const StatsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 30px;
  max-width: 800px;
  margin: 0 auto;
`;

const StatCard = styled.div`
  background: white;
  padding: 30px 20px;
  border-radius: ${props => props.theme.borderRadius.xl};
  box-shadow: ${props => props.theme.shadows.md};
  text-align: center;
  transition: transform ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.lg};
  }
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.theme.colors.primary};
  margin-bottom: 8px;
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.theme.colors.text.secondary};
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const Hero = () => {
  const [personalData, setPersonalData] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  useEffect(() => {
    const fetchPersonalData = async () => {
      try {
        const response = await fetch('/api/portfolio/personal');
        if (response.ok) {
          const data = await response.json();
          setPersonalData(data);
        }
      } catch (error) {
        console.error('Error fetching personal data:', error);
        // Fallback data
        setPersonalData({
          name: "Omprakash Utaha",
          title: "Senior Product Manager",
          location: "Bengaluru, Karnataka, India",
          summary: "Product | Fintech Enthusiast | Consumer Lending | CX",
          profileImage: "/images/profile.jpg"
        });
      }
    };

    fetchPersonalData();
  }, []);

  if (!personalData) {
    return <div>Loading...</div>;
  }

  return (
    <HeroContainer id="hero">
      <Container ref={ref}>
        <Badge
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <FaCoffee />
          Let's Connect
        </Badge>

        <MainTitle
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          Hi, I'm <span className="highlight">{personalData.name}</span>
        </MainTitle>

        <Subtitle
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          I craft intelligent systems with data, code & curiosity. 
          Transforming complex datasets into actionable insights and building AI-powered solutions 
          that drive real-world impact across industries.
        </Subtitle>

        <ActionButtons
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <Button
            href="#contact"
            variant="primary"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaDownload />
            Download Resume
          </Button>
          <Button
            href="https://www.linkedin.com/in/omprakashutaha-b1815136/"
            target="_blank"
            rel="noopener noreferrer"
            variant="secondary"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaCoffee />
            Let's Connect
          </Button>
        </ActionButtons>

        <StatsGrid
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <StatCard>
            <StatNumber>10+</StatNumber>
            <StatLabel>Years Experience</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>72%</StatNumber>
            <StatLabel>Automation Rate</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>10+</StatNumber>
            <StatLabel>Banks Integrated</StatLabel>
          </StatCard>
          <StatCard>
            <StatNumber>∞</StatNumber>
            <StatLabel>Curiosity Level</StatLabel>
          </StatCard>
        </StatsGrid>
      </Container>
    </HeroContainer>
  );
};

export default Hero;
