import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaEnvelope, FaDownload, FaLinkedin, FaGithub, FaArrowDown } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  background: ${props => props.theme.colors.background.hero};
  overflow: hidden;
  padding: ${props => props.theme.spacing.hero} 0;
`;

const BackgroundGradient = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: ${props => props.theme.gradients.primary};
  opacity: 0.1;
  z-index: 0;
`;

const HeroContent = styled.div`
  max-width: 1400px;
  width: 100%;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.xxl};
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.xxxl};
  align-items: center;
  position: relative;
  z-index: 2;
  min-height: 80vh;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: ${props => props.theme.spacing.xxl};
    padding: 0 ${props => props.theme.spacing.xl};
  }

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.lg};
    gap: ${props => props.theme.spacing.xl};
  }
`;

const HeroText = styled.div`
  color: ${props => props.theme.colors.text.inverse};
  text-align: left;
`;

const Greeting = styled(motion.div)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? '#f8fafc' : '#ffffff'};
  margin-bottom: ${props => props.theme.spacing.md};
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  letter-spacing: 0.025em;
`;

const Name = styled(motion.h1)`
  font-size: clamp(3rem, 6vw, 5rem);
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 50%, #e2e8f0 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: ${props => props.theme.spacing.lg};
  line-height: 1.1;
  letter-spacing: -0.02em;
  filter: drop-shadow(0 4px 8px rgba(0, 0, 0, 0.3));
`;

const Title = styled(motion.h2)`
  font-size: clamp(1.5rem, 3vw, 2.5rem);
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? '#e2e8f0' : '#ffffff'};
  margin-bottom: ${props => props.theme.spacing.xl};
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  letter-spacing: -0.01em;
`;

const Description = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  line-height: 1.8;
  margin-bottom: ${props => props.theme.spacing.xxl};
  color: ${props => props.isDarkMode ? '#f1f5f9' : '#ffffff'};
  opacity: 0.95;
  max-width: 600px;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
  font-weight: ${props => props.theme.fonts.weights.medium};
  text-align: left;
  white-space: pre-line;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    max-width: none;
    text-align: center;
  }
`;

const Stats = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.xxl};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    grid-template-columns: repeat(2, 1fr);
    gap: ${props => props.theme.spacing.md};
  }

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const Stat = styled.div`
  text-align: center;
`;

const StatNumber = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xxl};
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
  margin-bottom: ${props => props.theme.spacing.xs};
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  letter-spacing: -0.5px;
`;

const StatLabel = styled.div`
  font-size: ${props => props.theme.fonts.sizes.sm};
  color: ${props => props.isDarkMode ? '#ffffff' : '#f1f5f9'};
  opacity: 0.9;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
  font-weight: ${props => props.theme.fonts.weights.medium};
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.xxl};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    justify-content: center;
    flex-wrap: wrap;
    gap: ${props => props.theme.spacing.md};
  }
`;

const PrimaryButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  padding: ${props => props.theme.spacing.lg} ${props => props.theme.spacing.xxl};
  background: ${props => props.theme.colors.primary[600]};
  color: white;
  text-decoration: none;
  border-radius: ${props => props.theme.borderRadius.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  font-size: ${props => props.theme.fonts.sizes.md};
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.lg};
  border: none;
  cursor: pointer;

  &:hover {
    background: ${props => props.theme.colors.primary[700]};
    color: white;
    transform: translateY(-3px);
    box-shadow: ${props => props.theme.shadows.xl};
  }

  &:active {
    transform: translateY(-1px);
  }
`;

const SecondaryButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  padding: ${props => props.theme.spacing.lg} ${props => props.theme.spacing.xxl};
  background: transparent;
  color: ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
  text-decoration: none;
  border: 2px solid ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
  border-radius: ${props => props.theme.borderRadius.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  font-size: ${props => props.theme.fonts.sizes.md};
  transition: all ${props => props.theme.transitions.normal};
  cursor: pointer;

  &:hover {
    background: ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
    color: ${props => props.isDarkMode ? '#0a0a0a' : '#0a0a0a'};
    transform: translateY(-3px);
    box-shadow: ${props => props.theme.shadows.lg};
  }

  &:active {
    transform: translateY(-1px);
  }
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  gap: ${props => props.theme.spacing.lg};
`;

const SocialLink = styled(motion.a)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 56px;
  height: 56px;
  background: rgba(255, 255, 255, 0.1);
  color: ${props => props.theme.colors.text.inverse};
  border-radius: ${props => props.theme.borderRadius.full};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.lg};
  }

  svg {
    font-size: ${props => props.theme.fonts.sizes.lg};
  }
`;

const HeroVisual = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ProfileCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border-radius: ${props => props.theme.borderRadius.xxl};
  padding: ${props => props.theme.spacing.xxl};
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: ${props => props.theme.shadows.xl};
  max-width: 450px;
  width: 100%;
  margin: 0 auto;
  text-align: center;
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const ProfileImage = styled.div`
  width: 160px;
  height: 160px;
  border-radius: ${props => props.theme.borderRadius.full};
  margin: 0 auto ${props => props.theme.spacing.xl};
  overflow: hidden;
  border: 4px solid rgba(255, 255, 255, 0.3);
  box-shadow: ${props => props.theme.shadows.lg};

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: ${props => props.theme.borderRadius.full};
    transition: transform ${props => props.theme.transitions.normal};

    &:hover {
      transform: scale(1.05);
    }
  }
`;

const ProfileInfo = styled.div`
  color: ${props => props.theme.colors.text.inverse};
`;

const ProfileName = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  margin-bottom: ${props => props.theme.spacing.md};
  color: ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
`;

const ProfileRole = styled.p`
  font-size: ${props => props.theme.fonts.sizes.md};
  color: ${props => props.isDarkMode ? '#ffffff' : '#ffffff'};
  margin-bottom: ${props => props.theme.spacing.md};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
`;

const ProfileStats = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.lg};
  margin-top: ${props => props.theme.spacing.lg};
`;

const ProfileStat = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.lg};
  background: rgba(255, 255, 255, 0.1);
  border-radius: ${props => props.theme.borderRadius.lg};
  border: 1px solid rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    background: rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
  }
`;

const ScrollIndicator = styled(motion.div)`
  position: absolute;
  bottom: ${props => props.theme.spacing.xxl};
  left: 50%;
  transform: translateX(-50%);
  color: ${props => props.theme.colors.text.inverse};
  opacity: 0.7;
  cursor: pointer;
  z-index: 3;
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    opacity: 1;
    transform: translateX(-50%) translateY(-4px);
  }
`;

const Hero = () => {
  const { isDarkMode } = useTheme();

  const scrollToAbout = () => {
    document.getElementById('about')?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <HeroSection id="home">
      <BackgroundGradient />

      <HeroContent>
        <HeroText>
          <Greeting
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            isDarkMode={isDarkMode}
          >
            Hello World!!! 👋 I'm
          </Greeting>

          <Name
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            Omprakash Utaha
          </Name>

          <Title
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            isDarkMode={isDarkMode}
          >
            Product Lead
          </Title>

          <Description
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.8 }}
            isDarkMode={isDarkMode}
          >
            🚀 Product strategist. Business builder. Zero-to-one specialist. I thrive at the intersection of tech, data, and customer experience — scaling platforms from scratch to billions and crafting digital journeys that feel effortless.
          </Description>

          <Stats
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 1.0 }}
          >
            <Stat>
              <StatNumber isDarkMode={isDarkMode}>11+</StatNumber>
              <StatLabel isDarkMode={isDarkMode}>Years</StatLabel>
            </Stat>
            <Stat>
              <StatNumber isDarkMode={isDarkMode}>3</StatNumber>
              <StatLabel isDarkMode={isDarkMode}>Organizations</StatLabel>
            </Stat>
            <Stat>
              <StatNumber isDarkMode={isDarkMode}>35+</StatNumber>
              <StatLabel isDarkMode={isDarkMode}>Lenders</StatLabel>
            </Stat>
            <Stat>
              <StatNumber isDarkMode={isDarkMode}>10+</StatNumber>
              <StatLabel isDarkMode={isDarkMode}>Products</StatLabel>
            </Stat>
          </Stats>

          <ActionButtons
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 1.2 }}
          >
            <PrimaryButton
              href="#contact"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaEnvelope />
              Get In Touch
            </PrimaryButton>
            <SecondaryButton
              href="/CV_Omprakash_Utaha_2025.pdf"
              download="CV_Omprakash_Utaha_2025.pdf"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaDownload />
              Download CV
            </SecondaryButton>
          </ActionButtons>

          <SocialLinks
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 1.4 }}
          >
            <SocialLink
              href="https://www.linkedin.com/in/omprakash-utaha-b1815136/"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <FaLinkedin />
            </SocialLink>
            <SocialLink
              href="mailto:omprakashutaha@gmail.com"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <FaEnvelope />
            </SocialLink>
            <SocialLink
              href="https://github.com/OP3690"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <FaGithub />
            </SocialLink>
          </SocialLinks>
        </HeroText>

        <HeroVisual>
          <ProfileCard
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            whileHover={{ y: -10 }}
          >
            <ProfileImage>
              <img src="/images/profile.jpg" alt="Omprakash Utaha" />
            </ProfileImage>
            <ProfileInfo>
              <ProfileName isDarkMode={isDarkMode}>Omprakash Utaha</ProfileName>
              <ProfileRole isDarkMode={isDarkMode}>Product Lead</ProfileRole>
              <p style={{ color: '#ffffff', fontWeight: '500', textShadow: '0 1px 2px rgba(0, 0, 0, 0.3)' }}>Lendingkart • Bangalore, India 🇮🇳</p>

              <ProfileStats>
                <ProfileStat>
                  <div style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.25rem', color: '#ffffff' }}>5+</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8, color: '#ffffff' }}>PMs Mentored</div>
                </ProfileStat>
                <ProfileStat>
                  <div style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.25rem', color: '#ffffff' }}>250M+</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8, color: '#ffffff' }}>Revenue Growth</div>
                </ProfileStat>
                <ProfileStat>
                  <div style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.25rem', color: '#ffffff' }}>2</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8, color: '#ffffff' }}>0→1 Startups</div>
                </ProfileStat>
                <ProfileStat>
                  <div style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.25rem', color: '#ffffff' }}>4.5K+</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8, color: '#ffffff' }}>LinkedIn Followers</div>
                </ProfileStat>
              </ProfileStats>
            </ProfileInfo>
          </ProfileCard>
        </HeroVisual>
      </HeroContent>

      <ScrollIndicator
        onClick={scrollToAbout}
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
        whileHover={{ scale: 1.1 }}
      >
        <FaArrowDown size={24} />
      </ScrollIndicator>
    </HeroSection>
  );
};

export default Hero;
