import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaLinkedin, FaGithub, FaEnvelope, FaHeart, FaArrowUp } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const FooterContainer = styled.footer`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border-top: 1px solid rgba(255, 255, 255, 0.2);
  padding: ${props => props.theme.spacing.xxxl} 0 ${props => props.theme.spacing.xl};
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 50% 0%, rgba(102, 126, 234, 0.1) 0%, transparent 70%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.xl};
  position: relative;
  z-index: 2;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.lg};
  }
`;

const FooterContent = styled.div`
  display: grid;
  grid-template-columns: 1fr auto;
  gap: ${props => props.theme.spacing.xxxl};
  align-items: center;
  margin-bottom: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: ${props => props.theme.spacing.xl};
  }
`;

const FooterInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.md};
`;

const FooterLogo = styled.div`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};

  span {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
`;

const FooterDescription = styled.p`
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  font-size: ${props => props.theme.fonts.sizes.md};
  line-height: 1.7;
  max-width: 400px;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    max-width: none;
  }
`;

const FooterContact = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.sm};
`;

const ContactItem = styled.a`
  color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  text-decoration: none;
  font-size: ${props => props.theme.fonts.sizes.sm};
  transition: color ${props => props.theme.transitions.normal};

  &:hover {
    color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
  }
`;

const FooterSocial = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: ${props => props.theme.spacing.lg};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    align-items: center;
  }
`;

const SocialTitle = styled.h4`
  font-size: ${props => props.theme.fonts.sizes.md};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin: 0;
`;

const SocialLinks = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
`;

const SocialLink = styled(motion.a)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 45px;
  height: 45px;
  background: rgba(255, 255, 255, 0.1);
  color: ${props => props.isDarkMode ? '#ffffff' : '#2d3748'};
  border-radius: ${props => props.theme.borderRadius.full};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  border: 1px solid rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
    transform: translateY(-4px);
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
  }
`;

const FooterBottom = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: ${props => props.theme.spacing.xl};
  border-top: 1px solid rgba(255, 255, 255, 0.2);

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    flex-direction: column;
    gap: ${props => props.theme.spacing.md};
    text-align: center;
  }
`;

const Copyright = styled.p`
  color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  font-size: ${props => props.theme.fonts.sizes.sm};
  margin: 0;
`;

const CopyrightHeart = styled.span`
  color: #ef4444;
  margin: 0 ${props => props.theme.spacing.xs};
`;

const ScrollToTop = styled(motion.button)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 45px;
  height: 45px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: ${props => props.theme.borderRadius.full};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 24px rgba(102, 126, 234, 0.4);
  }
`;

const Footer = () => {
  const { isDarkMode } = useTheme();

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <FooterContainer>
      <Container>
        <FooterContent>
          <FooterInfo>
            <FooterLogo isDarkMode={isDarkMode}>
              Omprakash<span>.</span>
            </FooterLogo>
            <FooterDescription isDarkMode={isDarkMode}>
              Product Lead with 10+ years of experience transforming fintech through innovative solutions. 
              Passionate about scaling products and driving business growth.
            </FooterDescription>
            <FooterContact>
              <ContactItem href="mailto:omprakashutaha@gmail.com" isDarkMode={isDarkMode}>
                omprakashutaha@gmail.com
              </ContactItem>
              <ContactItem href="tel:+919723231499" isDarkMode={isDarkMode}>
                +91 97232 31499
              </ContactItem>
              <ContactItem isDarkMode={isDarkMode}>
                Bangalore, Karnataka, India
              </ContactItem>
            </FooterContact>
          </FooterInfo>

          <FooterSocial>
            <SocialTitle isDarkMode={isDarkMode}>Connect with me</SocialTitle>
            <SocialLinks>
              <SocialLink
                href="https://www.linkedin.com/in/omprakash-utaha-b1815136/"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="LinkedIn"
                isDarkMode={isDarkMode}
              >
                <FaLinkedin />
              </SocialLink>
              <SocialLink
                href="https://github.com/OP3690"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="GitHub"
                isDarkMode={isDarkMode}
              >
                <FaGithub />
              </SocialLink>
              <SocialLink
                href="mailto:omprakashutaha@gmail.com"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="Email"
                isDarkMode={isDarkMode}
              >
                <FaEnvelope />
              </SocialLink>
            </SocialLinks>
          </FooterSocial>
        </FooterContent>

        <FooterBottom>
          <Copyright isDarkMode={isDarkMode}>
            © 2025 Omprakash Utaha. Made with<CopyrightHeart><FaHeart /></CopyrightHeart>in India
          </Copyright>
          <ScrollToTop
            onClick={scrollToTop}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            aria-label="Scroll to top"
          >
            <FaArrowUp />
          </ScrollToTop>
        </FooterBottom>
      </Container>
    </FooterContainer>
  );
};

export default Footer;
