import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaMapMarkerAlt, FaBriefcase, FaChartLine } from 'react-icons/fa';

const AboutSection = styled.section`
  padding: 100px 0;
  background: ${({ theme }) => theme.colors.surfaceDark};
  position: relative;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 4rem;
`;

const SectionTitle = styled.h2`
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 700;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, ${({ theme }) => theme.colors.text} 0%, ${({ theme }) => theme.colors.primary} 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled.p`
  font-size: 1.1rem;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-weight: 500;
`;

const AboutGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 4rem;
  align-items: start;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const AboutContent = styled.div``;

const AboutText = styled.div`
  p {
    margin-bottom: 1.5rem;
    font-size: 1.1rem;
    line-height: 1.8;
    color: ${({ theme }) => theme.colors.textSecondary};
  }

  strong {
    color: ${({ theme }) => theme.colors.primary};
    font-weight: 600;
  }
`;

const AboutHighlights = styled.div`
  margin-top: 2rem;
`;

const HighlightItem = styled(motion.div)`
  display: flex;
  align-items: flex-start;
  gap: 1rem;
  margin-bottom: 1.5rem;
  padding: 1rem;
  background: ${({ theme }) => theme.colors.surface};
  border-radius: 12px;
  border: 1px solid ${({ theme }) => theme.colors.surfaceLight};
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const HighlightIcon = styled.div`
  color: ${({ theme }) => theme.colors.primary};
  font-size: 1.2rem;
  margin-top: 0.2rem;
`;

const HighlightContent = styled.div`
  h4 {
    color: ${({ theme }) => theme.colors.text};
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  p {
    color: ${({ theme }) => theme.colors.textSecondary};
    font-size: 0.9rem;
  }
`;

const AboutVisual = styled.div``;

const EducationCards = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const EduCard = styled(motion.div)`
  background: ${({ theme }) => theme.colors.surface};
  border-radius: 16px;
  padding: 1.5rem;
  border: 1px solid ${({ theme }) => theme.colors.surfaceLight};
  display: flex;
  align-items: center;
  gap: 1rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const EduLogo = styled.img`
  width: 60px;
  height: 60px;
  object-fit: contain;
  border-radius: 8px;
`;

const EduInfo = styled.div`
  h4 {
    color: ${({ theme }) => theme.colors.text};
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  p {
    color: ${({ theme }) => theme.colors.primary};
    font-size: 0.9rem;
    margin-bottom: 0.25rem;
  }

  span {
    color: ${({ theme }) => theme.colors.textMuted};
    font-size: 0.8rem;
  }
`;

const About = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const [portfolioData, setPortfolioData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/portfolio');
        const data = await response.json();
        setPortfolioData(data);
      } catch (error) {
        console.error('Error fetching portfolio data:', error);
        // Fallback data
        setPortfolioData({
          personal: {
            location: "Bengaluru, Karnataka, India",
            summary: "I'm a Senior Product Manager with over a decade of experience in Product Management, specializing in digitizing financial products and streamlining operations to deliver scalable solutions."
          },
          education: [
            {
              institution: "Gujarat University",
              degree: "Bachelor's in Computer Science",
              period: "2010 - 2013",
              logo: "/images/gujarat-university.png"
            },
            {
              institution: "GAYATRI VIDHYA VIHAR",
              degree: "HSC, Science",
              period: "2006 - 2010",
              logo: "/images/school-board.png"
            }
          ]
        });
      }
    };

    fetchData();
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  const highlights = [
    {
      icon: <FaMapMarkerAlt />,
      title: "Location",
      description: portfolioData?.personal?.location || "Bengaluru, Karnataka, India"
    },
    {
      icon: <FaBriefcase />,
      title: "Current Role",
      description: "Senior Product Manager at Lendingkart"
    },
    {
      icon: <FaChartLine />,
      title: "Impact",
      description: "â‚¹10,000+ Cr in Loan Disbursements"
    }
  ];

  return (
    <AboutSection id="about">
      <Container ref={ref}>
        <SectionHeader
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <SectionTitle variants={itemVariants}>About Me</SectionTitle>
          <SectionSubtitle variants={itemVariants}>
            Passionate about revolutionizing financial technology
          </SectionSubtitle>
        </SectionHeader>

        <AboutGrid>
          <AboutContent>
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate={inView ? "visible" : "hidden"}
            >
              <AboutText variants={itemVariants}>
                <p>
                  I'm a <strong>Senior Product Manager</strong> with over a decade of experience in Product Management, 
                  specializing in digitizing financial products and streamlining operations to deliver scalable solutions.
                </p>
                <p>
                  My journey includes automating credit underwriting by <strong>72%</strong>, optimizing entire loan journeys 
                  from onboarding to collections, and integrating LOS and LMS systems across <strong>10+ Banks and NBFCs</strong> 
                  for cutting-edge co-lending arrangements.
                </p>
                <p>
                  With a user-first mindset and data-driven approach, I craft seamless UI/UX experiences that delight users 
                  while delivering measurable business impact. I thrive on solving complex challenges and transforming ideas 
                  into tangible results.
                </p>
                <p>
                  Currently excited about AI, LLMs, and futuristic technology, I'm passionate about pushing boundaries 
                  and shaping the future of product innovation in fintech.
                </p>
              </AboutText>

              <AboutHighlights variants={itemVariants}>
                {highlights.map((highlight, index) => (
                  <HighlightItem
                    key={index}
                    whileHover={{ y: -5 }}
                    transition={{ duration: 0.3 }}
                  >
                    <HighlightIcon>{highlight.icon}</HighlightIcon>
                    <HighlightContent>
                      <h4>{highlight.title}</h4>
                      <p>{highlight.description}</p>
                    </HighlightContent>
                  </HighlightItem>
                ))}
              </AboutHighlights>
            </motion.div>
          </AboutContent>

          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate={inView ? "visible" : "hidden"}
          >
            <AboutVisual>
              <EducationCards variants={itemVariants}>
                {portfolioData?.education?.map((edu, index) => (
                  <EduCard
                    key={index}
                    whileHover={{ y: -5 }}
                    transition={{ duration: 0.3 }}
                  >
                    <EduLogo src={edu.logo} alt={edu.institution} />
                    <EduInfo>
                      <h4>{edu.institution}</h4>
                      <p>{edu.degree}</p>
                      <span>{edu.period}</span>
                    </EduInfo>
                  </EduCard>
                ))}
              </EducationCards>
