import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaEnvelope, FaPhone, FaMapMarkerAlt, FaLinkedin, FaGithub, FaPaperPlane } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const ContactSection = styled.section`
  padding: ${props => props.theme.spacing.section} 0;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.05) 0%, rgba(255, 255, 255, 0.02) 100%);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 40% 60%, rgba(102, 126, 234, 0.08) 0%, transparent 50%),
                radial-gradient(circle at 60% 40%, rgba(118, 75, 162, 0.08) 0%, transparent 50%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.xl};
  position: relative;
  z-index: 2;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.lg};
  }
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xxxl};
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: ${props => props.theme.fonts.weights.extrabold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.lg};
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const SectionSubtitle = styled(motion.p)`
  font-size: ${props => props.theme.fonts.sizes.lg};
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.8;
  opacity: 0.9;
`;

const ContactGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.xxxl};
  max-width: 1200px;
  margin: 0 auto;

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.xxl};
  }
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.xl};
`;

const ContactCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing.xxl};
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-8px);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }
`;

const ContactHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const ContactIcon = styled.div`
  width: 50px;
  height: 50px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: ${props => props.theme.borderRadius.lg};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${props => props.theme.fonts.sizes.lg};
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
`;

const ContactTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin: 0;
`;

const ContactContent = styled.div`
  color: ${props => props.isDarkMode ? '#cbd5e1' : '#475569'};
  line-height: 1.7;
`;

const ContactForm = styled(motion.form)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.xl};
  padding: ${props => props.theme.spacing.xxl};
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }
`;

const FormTitle = styled.h3`
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  margin-bottom: ${props => props.theme.spacing.lg};
  text-align: center;
`;

const FormGroup = styled.div`
  margin-bottom: ${props => props.theme.spacing.lg};
`;

const Label = styled.label`
  display: block;
  font-size: ${props => props.theme.fonts.sizes.md};
  font-weight: ${props => props.theme.fonts.weights.medium};
  color: ${props => props.isDarkMode ? '#e2e8f0' : '#334155'};
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const Input = styled.input`
  width: 100%;
  padding: ${props => props.theme.spacing.md};
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.lg};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  font-size: ${props => props.theme.fonts.sizes.md};
  transition: all ${props => props.theme.transitions.normal};

  &::placeholder {
    color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
    box-shadow: 0 0 0 3px rgba(96, 165, 250, 0.1);
    background: rgba(255, 255, 255, 0.15);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: ${props => props.theme.spacing.md};
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.lg};
  color: ${props => props.isDarkMode ? '#ffffff' : '#0f172a'};
  font-size: ${props => props.theme.fonts.sizes.md};
  min-height: 120px;
  resize: vertical;
  transition: all ${props => props.theme.transitions.normal};

  &::placeholder {
    color: ${props => props.isDarkMode ? '#94a3b8' : '#64748b'};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
    box-shadow: 0 0 0 3px rgba(96, 165, 250, 0.1);
    background: rgba(255, 255, 255, 0.15);
  }
`;

const SubmitButton = styled(motion.button)`
  width: 100%;
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.xl};
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: ${props => props.theme.borderRadius.lg};
  font-size: ${props => props.theme.fonts.sizes.md};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.normal};
  display: flex;
  align-items: center;
  justify-content: center;
  gap: ${props => props.theme.spacing.sm};
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(102, 126, 234, 0.4);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-top: ${props => props.theme.spacing.lg};
  justify-content: center;
`;

const SocialLink = styled(motion.a)`
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: ${props => props.theme.borderRadius.full};
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.isDarkMode ? '#ffffff' : '#2d3748'};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};
  backdrop-filter: blur(10px);

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-4px);
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
    color: ${props => props.isDarkMode ? '#60a5fa' : '#2563eb'};
  }
`;

const Contact = () => {
  const { isDarkMode } = useTheme();
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: ''
      });
      alert('Thank you for your message! I will get back to you soon.');
    }, 2000);
  };

  return (
    <ContactSection id="contact">
      <Container>
        <SectionHeader
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <SectionTitle isDarkMode={isDarkMode}>Get In Touch</SectionTitle>
          <SectionSubtitle isDarkMode={isDarkMode}>
            Ready to collaborate on your next project? Let's discuss how we can work together to bring your ideas to life.
          </SectionSubtitle>
        </SectionHeader>

        <ContactGrid ref={ref}>
          <ContactInfo
            initial={{ opacity: 0, x: -30 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <ContactCard>
              <ContactHeader>
                <ContactIcon>
                  <FaEnvelope />
                </ContactIcon>
                <ContactTitle isDarkMode={isDarkMode}>Email</ContactTitle>
              </ContactHeader>
              <ContactContent isDarkMode={isDarkMode}>
                <p>omprakashutaha@gmail.com</p>
                <p>I typically respond within 24 hours</p>
              </ContactContent>
            </ContactCard>

            <ContactCard>
              <ContactHeader>
                <ContactIcon>
                  <FaMapMarkerAlt />
                </ContactIcon>
                <ContactTitle isDarkMode={isDarkMode}>Location</ContactTitle>
              </ContactHeader>
              <ContactContent isDarkMode={isDarkMode}>
                <p>Bangalore, India 🇮🇳</p>
                <p>Available for remote work worldwide</p>
              </ContactContent>
            </ContactCard>

            <ContactCard>
              <ContactHeader>
                <ContactIcon>
                  <FaPhone />
                </ContactIcon>
                <ContactTitle isDarkMode={isDarkMode}>Availability</ContactTitle>
              </ContactHeader>
              <ContactContent isDarkMode={isDarkMode}>
                <p>Currently accepting new opportunities</p>
                <p>Open to consulting and full-time roles</p>
              </ContactContent>
            </ContactCard>

            <div>
              <h4 style={{ 
                color: isDarkMode ? '#ffffff' : '#0f172a', 
                marginBottom: '1rem',
                fontSize: '1.1rem',
                fontWeight: '600'
              }}>
                Connect with me
              </h4>
              <SocialLinks>
                <SocialLink
                  href="https://www.linkedin.com/in/omprakash-utaha-b1815136/"
                  target="_blank"
                  rel="noopener noreferrer"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  isDarkMode={isDarkMode}
                >
                  <FaLinkedin />
                </SocialLink>
                <SocialLink
                  href="https://github.com/OP3690"
                  target="_blank"
                  rel="noopener noreferrer"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  isDarkMode={isDarkMode}
                >
                  <FaGithub />
                </SocialLink>
              </SocialLinks>
            </div>
          </ContactInfo>

          <ContactForm
            initial={{ opacity: 0, x: 30 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.6, delay: 0.4 }}
            onSubmit={handleSubmit}
          >
            <FormTitle isDarkMode={isDarkMode}>Send me a message</FormTitle>

            <FormGroup>
              <Label isDarkMode={isDarkMode}>Name</Label>
              <Input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Your name"
                required
                isDarkMode={isDarkMode}
              />
            </FormGroup>

            <FormGroup>
              <Label isDarkMode={isDarkMode}>Email</Label>
              <Input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="your.email@example.com"
                required
                isDarkMode={isDarkMode}
              />
            </FormGroup>

            <FormGroup>
              <Label isDarkMode={isDarkMode}>Subject</Label>
              <Input
                type="text"
                name="subject"
                value={formData.subject}
                onChange={handleInputChange}
                placeholder="What's this about?"
                required
                isDarkMode={isDarkMode}
              />
            </FormGroup>

            <FormGroup>
              <Label isDarkMode={isDarkMode}>Message</Label>
              <TextArea
                name="message"
                value={formData.message}
                onChange={handleInputChange}
                placeholder="Tell me about your project or opportunity..."
                required
                isDarkMode={isDarkMode}
              />
            </FormGroup>

            <SubmitButton
              type="submit"
              disabled={isSubmitting}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              {isSubmitting ? (
                <>
                  <div style={{ width: '16px', height: '16px', border: '2px solid transparent', borderTop: '2px solid white', borderRadius: '50%', animation: 'spin 1s linear infinite' }} />
                  Sending...
                </>
              ) : (
                <>
                  <FaPaperPlane />
                  Send Message
                </>
              )}
            </SubmitButton>
          </ContactForm>
        </ContactGrid>
      </Container>
    </ContactSection>
  );
};

export default Contact;
