import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaSun, FaMoon, FaBars, FaTimes, FaHome, FaUser, FaBriefcase, FaCode, FaEnvelope } from 'react-icons/fa';
import { useTheme } from '../styles/ThemeContext';

const NavContainer = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  transition: all ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.shadows.glass};
`;

const NavContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${props => props.theme.spacing.lg};
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 70px;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    padding: 0 ${props => props.theme.spacing.md};
  }
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.bold};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  text-decoration: none;
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    transform: translateY(-1px);
  }
`;

const OUInitials = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  background: ${props => props.theme.gradients.primary};
  color: white;
  border-radius: ${props => props.theme.borderRadius.full};
  font-size: ${props => props.theme.fonts.sizes.sm};
  font-weight: ${props => props.theme.fonts.weights.bold};
  box-shadow: ${props => props.theme.shadows.md};
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: scale(1.1);
    box-shadow: ${props => props.theme.shadows.lg};
  }
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    display: none;
  }
`;

const NavLink = styled.a`
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.secondary : props.theme.colors.light.text.secondary};
  text-decoration: none;
  font-weight: ${props => props.theme.fonts.weights.medium};
  transition: all ${props => props.theme.transitions.normal};
  position: relative;
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border-radius: ${props => props.theme.borderRadius.lg};

  &:hover {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
    transform: translateY(-1px);
  }

  &.active {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};

    &::after {
      content: '';
      position: absolute;
      bottom: -2px;
      left: 50%;
      transform: translateX(-50%);
      width: 20px;
      height: 2px;
      background: ${props => props.theme.gradients.primary};
      border-radius: ${props => props.theme.borderRadius.full};
    }
  }
`;

const ThemeToggle = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  border: 1px solid ${props => props.isDarkMode ? props.theme.colors.dark.border.primary : props.theme.colors.light.border.primary};
  border-radius: ${props => props.theme.borderRadius.full};
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.normal};
  backdrop-filter: blur(10px);

  &:hover {
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
    transform: scale(1.1);
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  flex-direction: column;
  justify-content: space-around;
  width: 30px;
  height: 30px;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 10;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    display: flex;
  }

  span {
    width: 100%;
    height: 3px;
    background: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
    border-radius: ${props => props.theme.borderRadius.full};
    transition: all ${props => props.theme.transitions.normal};
    transform-origin: 1px;

    &:first-child {
      transform: ${props => props.isOpen ? 'rotate(45deg)' : 'rotate(0)'};
    }

    &:nth-child(2) {
      opacity: ${props => props.isOpen ? '0' : '1'};
      transform: ${props => props.isOpen ? 'translateX(20px)' : 'translateX(0)'};
    }

    &:nth-child(3) {
      transform: ${props => props.isOpen ? 'rotate(-45deg)' : 'rotate(0)'};
    }
  }
`;

const MobileMenu = styled.div`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glass : props.theme.colors.light.background.glass};
  backdrop-filter: blur(20px);
  z-index: 999;
  padding-top: 70px;

  @media (max-width: ${props => props.theme.breakpoints.md}) {
    display: ${props => props.isOpen ? 'flex' : 'none'};
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    padding-top: 100px;
  }
`;

const MobileNavLink = styled.a`
  color: ${props => props.isDarkMode ? props.theme.colors.dark.text.primary : props.theme.colors.light.text.primary};
  text-decoration: none;
  font-size: ${props => props.theme.fonts.sizes.xl};
  font-weight: ${props => props.theme.fonts.weights.semibold};
  padding: ${props => props.theme.spacing.lg};
  transition: all ${props => props.theme.transitions.normal};
  border-radius: ${props => props.theme.borderRadius.lg};
  margin: ${props => props.theme.spacing.sm} 0;

  &:hover {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
    transform: translateY(-2px);
  }

  &.active {
    color: ${props => props.isDarkMode ? props.theme.colors.dark.text.accent : props.theme.colors.light.text.accent};
    background: ${props => props.isDarkMode ? props.theme.colors.dark.background.glassHover : props.theme.colors.light.background.glassHover};
  }
`;

const navItems = [
  { id: 'home', label: 'Home', icon: FaHome, href: '#home' },
  { id: 'about', label: 'About', icon: FaUser, href: '#about' },
  { id: 'experience', label: 'Experience', icon: FaBriefcase, href: '#experience' },
  { id: 'skills', label: 'Skills', icon: FaCode, href: '#skills' },
  { id: 'contact', label: 'Contact', icon: FaEnvelope, href: '#contact' }
];

const Navbar = () => {
  const { isDarkMode, toggleTheme } = useTheme();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const sections = navItems.map(item => item.id);
      const scrollPosition = window.scrollY + 100;

      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const offsetTop = element.offsetTop;
          const offsetHeight = element.offsetHeight;

          if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (href) => {
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMobileMenuOpen(false);
  };

  return (
    <>
      <NavContainer
        isDarkMode={isDarkMode}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5, ease: "easeOut" }}
      >
        <NavContent>
                     <Logo
             whileHover={{ scale: 1.05 }}
             whileTap={{ scale: 0.95 }}
             onClick={() => scrollToSection('#home')}
           >
             <OUInitials>OU</OUInitials>
             Omprakash Utaha
           </Logo>

          <NavLinks>
            {navItems.map((item) => (
              <NavLink
                key={item.id}
                href={item.href}
                className={activeSection === item.id ? 'active' : ''}
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection(item.href);
                }}
                whileHover={{ y: -2 }}
                whileTap={{ scale: 0.95 }}
              >
                <item.icon />
                {item.label}
              </NavLink>
            ))}
          </NavLinks>

          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <ThemeToggle
              onClick={toggleTheme}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {isDarkMode ? <FaSun /> : <FaMoon />}
            </ThemeToggle>

            <MobileMenuButton
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              whileTap={{ scale: 0.9 }}
            >
              {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
            </MobileMenuButton>
          </div>
        </NavContent>
      </NavContainer>

      <AnimatePresence>
        {isMobileMenuOpen && (
          <MobileMenu
            isOpen={isMobileMenuOpen}
            isDarkMode={isDarkMode}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.2 }}
          >
            {navItems.map((item) => (
              <MobileNavLink
                key={item.id}
                href={item.href}
                className={activeSection === item.id ? 'active' : ''}
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection(item.href);
                }}
                whileHover={{ x: 10 }}
                whileTap={{ scale: 0.95 }}
              >
                <item.icon />
                {item.label}
              </MobileNavLink>
            ))}
          </MobileMenu>
        )}
      </AnimatePresence>
    </>
  );
};

export default Navbar;
